// Automatically generated by PRGA's RTL generator
{% set width = module.ports.i|length -%}
{% set sel_width = module.ports.sel|length -%}
`timescale 1ns/1ps
module {{ module.name }} (
    input wire [{{ width - 1 }}:0] i
    , output reg [0:0] o

    , input wire [0:0] cfg_e
    , input wire [{{ sel_width - 1}}:0] sel
    );

    always @* begin
        if (cfg_e) begin
            o = 1'b0;
        end else begin
            o = 1'b0;
            case (sel)
                {%- for i in range(width) %}
                {{ sel_width }}'d{{ i }}: o = i[{{ i }}];
                {%- endfor %}
            endcase
        end
    end

endmodule
