// Automatically generated by PRGA's blackbox library generator
module {{ module.name }} (
    {%- set comma = joiner(",") %}
    {%- for port in itervalues(module.ports) %}{{ comma() }}
    {{ port.direction.case('input', 'output') }} wire [{{ port.width - 1}}:0] {{ port.name }}
    {%- endfor %}
    );
    {%- for param, info in iteritems(module.parameters) %}
    parameter {{ param }} = {{ info.init|default('""') }};
    {%- endfor %}
endmodule

